import React, { useContext, useEffect, useState } from "react";
import { AppContext} from '../App.js'
import { Cards } from 'react-responsive-cards';
import first from './first.jpg';
import second from './second.jpg';
import third from './third.jpg';


export default function Communitylist() {
    
    let valueFromFormInput = useContext(AppContext)
    const [data, setData] = useState("");
    useEffect(() => {
        fetch(`https://api.giphy.com/v1/gifs/search?api_key=YIjqVJb0IXEM2sdLQA5F1xMQbVRDlGME&q=${valueFromFormInput.input}&limit=3&rating=g`)
            .then(res => res.json())
            .then(result => {
                console.log(result);
                let map1 = result.data.map(v => v.images.original.mp4)
                console.log(map1)
                setData(map1)
            })
    }, []);


    // const [list, setList] = useState("");

    const details = [
        {
            title: 'Emotional Support',
            image: { first }
        }, {
            title: 'Professional Etiquette',
            image: { second }
        },
        {
            title: 'Latest Industry News',
            image: { third }
        }
    ]

    let map1 = details.map(v => {return <img src={v.image} alt="1" id="img"></img> })

    return (
        <div>
            <label htmlFor="searchInput">Enter a Search Term </label>
            <input type="text" className="form-control" id="searchInput" onChange={(event) => {
                valueFromFormInput.useInputValueForFetch()
            }} />
            <button type="button" className="btn btn-success" onClick={() => {
                valueFromFormInput.processInputUsingButton()
            }}>Search</button>
            <ul>{data}</ul>
            <div>{map1}</div>
           <div><Cards details={details} /></div> 
        </div>
    )
}
