import Navbar from "./components/Navbar";
import Footer from "./components/Footer";
import React from 'react';
import Community from "./components/Community";
import { useState, createContext } from "react";


export const AppContext = createContext(null);

const [text, getInput] = useState("");//onchange
const [input, setInput] = useState("");//buttons

const useInputValueForFetch = (event) => {
  event.preventDefault()
  let textFromForm = event.target.value;
  getInput(textFromForm)
}

const processInputUsingButton = () => {
  setInput(text)
}

function App() {
  return (
    <div>
      <Navbar />
      <div><Community/></div>
      <AppContext.Provider value={{ text, input, getInput, setInput, useInputValueForFetch, processInputUsingButton }}/>
      <div>
        <Footer />
      </div>
    </div>


  )
}



export default App;
